import './App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';
import sky from '../public/images/nublado.jpg'

function App() {
  const [weatherData, setWeatherData] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      position => {
        const { latitude, longitude } = position.coords;
        axios
          .get(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=97d54f6f736bc4d9d7014516da54aa0c`)
          .then(res => {
            setWeatherData(res.data);
          })
          .catch(error => {
            setErrorMessage(error.message);
          });
      },
      error => {
        setErrorMessage(error.message);
      }
    );
  }, []);

  return (
    <div className='app'>
      <img src={sky} alt="a image of sky with clouds"></img>
      {weatherData ? (
        <p>
          The current temperature is {(weatherData.main.temp - 273.15).toFixed(2)}°C in {weatherData.name}.
        </p>
      ) : errorMessage ? (
        <p>{errorMessage}</p>
      ) : (
        <p>Loading weather data...</p>
      )}
    </div>
  );
};

export default App;


