import './App.css';
import axios from 'axios';
import React from 'react';
import { useState, useEffect } from 'react';
import sky from './images/nublado.jpg'
import { BsCloudSun, BsCloudMoon,  BsCloudRain, BsCloudLightningRain, BsCloudSnow } from 'react-icons/bs'

function App() {
  const [weatherData, setWeatherData] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);
  let date = new Date();
  let hours = date.getHours();
  let minutes = date.getMinutes();
  let formattedTime = `${hours}:${minutes}`;

  const currentHour = new Date().getHours();
  let icon;

  if (currentHour >= 6 && currentHour < 18) {
    icon = <BsCloudSun className="icon" />;
  } else {
    icon = <BsCloudMoon className="icon" />;
  }

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      position => {
        const { latitude, longitude } = position.coords;
        axios
          .get(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=97d54f6f736bc4d9d7014516da54aa0c`)
          .then(res => {
            setWeatherData(res.data);
          })
          .catch(error => {
            setErrorMessage(error.message);
          });
      },
      error => {
        setErrorMessage(error.message);
      }
    );
  }, []);

  return (
    <div className='app'>
      <h1>WEATHER SITE</h1>
      {weatherData ? (
        <><div>
          <h2>
            {weatherData.name}, {formattedTime}
          </h2>
        </div><div>
            <h3>
              {(weatherData.main.temp - 273.15).toFixed(0)} °C
            </h3>
            {icon}
          </div></>
      ) : errorMessage ? (
        <p>{errorMessage}</p>
      ) : (
        <p>Loading weather data...</p>
      )}
    </div>
  );
};

export default App;


