import './App.css';
import axios from 'axios';
import React from 'react';
import { useState, useEffect } from 'react';
import sky from './images/nublado.jpg'

function App() {
  const [weatherData, setWeatherData] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);
  let date = new Date();
  let hours = date.getHours();
  let minutes = date.getMinutes();
  let formattedTime = `${hours}:${minutes}`;

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      position => {
        const { latitude, longitude } = position.coords;
        axios
          .get(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=97d54f6f736bc4d9d7014516da54aa0c`)
          .then(res => {
            setWeatherData(res.data);
          })
          .catch(error => {
            setErrorMessage(error.message);
          });
      },
      error => {
        setErrorMessage(error.message);
      }
    );
  }, []);



  return (
    <div className='app'>
      <img src={sky} alt="a sky with clouds"></img>
      <h1>WEATHER SITE</h1>
      {weatherData ? (
        <><div>
          <h2>
            {weatherData.name}
          </h2>
        </div><div>
            <h3>
              {(weatherData.main.temp - 273.15).toFixed(0)} °C {formattedTime}
            </h3>
          </div></>
      ) : errorMessage ? (
        <p>{errorMessage}</p>
      ) : (
        <p>Loading weather data...</p>
      )}
    </div>
  );
};

export default App;


